import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { Button, StyleSheet, Text, View, TouchableHighlight, Alert, TextInput, ScrollView, FlatList } from 'react-native';
import GoalItem from './components/GoalItem';
import GoalInput from './components/GoalInput';

export default function App() {
  const [courseGoals, setCourseGoals] = useState([]);
  function onDeleteItem(id) {
    setCourseGoals((currentCourseGoals) => {
      return currentCourseGoals.filter((goal) => goal.id !== id);
    });
  }


  function addGoalHandler(enteredGoalText) {
    setCourseGoals(currentCourseGoals =>
      [
        ...currentCourseGoals,
        { id: Math.random().toString(), value: enteredGoalText },
      ]
    );
  }

  return (
    <View style={styles.container}>
      <GoalInput 
        onAddGoal ={addGoalHandler}
      />
      <View style={styles.goalsContainer}>
        <FlatList
          data={courseGoals}
          keyExtractor={(item, index) => index.toString()}
          renderItem={({ item }) => (
            <GoalItem item={item} 
            id={item.id}
            deleteGoalHandler={onDeleteItem}/>
          )}
        />


      </View>
      <FlatList

      />
      {/* <View style={styles.buttonContainer}>
        <TouchableHighlight style={styles.button} onPress={() => Alert.alert('Clicked')}>
          <Text style={styles.buttonText}>Click</Text>
        </TouchableHighlight>
      </View> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    marginTop: 20,
    paddingTop: 50,
    alignItems: 'center',
  },
  flexStyle: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginVertical: 5,
  },
  textInput: {
    width: '50%',
    height: '70%',
    marginEnd: 10,
    borderWidth: 1,
    borderColor: '#cccccc',
    borderRadius: 4,
    paddingHorizontal: 4,
  },
  buttonContainer: {
    marginVertical: 10,
    backgroundColor: '#000000',
    borderRadius: 4,
    width: 120,
    height: 40,
  },
  button: {
    alignItems: 'center',
    justifyContent: 'center',
    flex: 1,
  },
  buttonText: {
    color: '#fff',
  },
  goalsContainer: {
    // flex: 1,
  },
  goalItems: {
    marginVertical: 5,
    borderRadius: 5,
    backgroundColor: 'blue',
    color: 'white',
    padding: 10,
    width: '100%',
  },
  goalItemsText: {
    color: 'white',
  },
});
