import { View, Text, StyleSheet, TextInput, TouchableHighlight, Modal } from 'react-native'
import React, { useState } from 'react'
export default function GoalInput(props) {
    const [enteredGoalText, setEnteredGoalText] = useState('');
    function goalInputHandler(enteredText) {
        setEnteredGoalText(enteredText);
    }
    function addGoalHandler() {
        props.onAddGoal(enteredGoalText);
    }
    return (
        <Modal visible={props.visible} animationType='slide'>
            <View style={styles.flexStyle}>
                <TextInput style={styles.textInput} onChangeText={goalInputHandler} placeholder='Your course goal' />
                <View style={styles.buttonContainer}>
                    <TouchableHighlight style={styles.button} onPress={addGoalHandler}>
                        <Text style={styles.buttonText}>Add Goal</Text>
                    </TouchableHighlight>
                    <TouchableHighlight style={styles.button} onPress={addGoalHandler}>
                        <Text style={styles.buttonText}>Cancel</Text>
                    </TouchableHighlight>
                </View>
            </View>
        </Modal>
    )
}
const styles = StyleSheet.create({

    flexStyle: {
        alignItems: 'center',
        marginVertical: 5,
        justifyContent: 'center'
    },
    textInput: {
        width: '50%',
        height: '50%',
        marginEnd: 10,
        borderWidth: 1,
        borderColor: '#cccccc',
        borderRadius: 4,
        paddingHorizontal: 4,
    },
    buttonContainer: {
        marginVertical: 10,
        backgroundColor: '#000000',
        borderRadius: 4,
        width: 120,
        height: 40,
        flex: 1,
        flexDirection: "row",
    },
    button: {
        alignItems: 'center',
        justifyContent: 'center',
        flex: 1,
        marginHorizontal: 5,
    },
    buttonText: {
        color: '#fff',
    },
});
