import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { Button, StyleSheet, Text, View, TouchableHighlight, Alert, TextInput, ScrollView, FlatList } from 'react-native';
import GoalItem from './components/GoalItem';
import GoalInput from './components/GoalInput';

export default function App() {
  const [courseGoals, setCourseGoals] = useState([]);
  const [modalIsVisible, setModalIsVisible] = useState(false);
  function onDeleteItem(id) {
    setCourseGoals((currentCourseGoals) => {
      return currentCourseGoals.filter((goal) => goal.id !== id);
    });
  }
  function startAddGoalHandler() {
    setModalIsVisible(true);
  }

  function addGoalHandler(enteredGoalText) {
    setCourseGoals(currentCourseGoals =>
      [
        ...currentCourseGoals,
        { id: Math.random().toString(), value: enteredGoalText },
      ]
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.buttonContainer}>
        <TouchableHighlight style={styles.button} onPress={startAddGoalHandler}>
          <Text style={styles.buttonText}>Add New Goal</Text>
        </TouchableHighlight>
      </View>
      {modalIsVisible && <GoalInput
        visible={modalIsVisible}
        onAddGoal={addGoalHandler}
      />}
      <View style={styles.goalsContainer}>
        <FlatList
          data={courseGoals}
          keyExtractor={(item, index) => index.toString()}
          renderItem={({ item }) => (
            <GoalItem item={item}
              id={item.id}
              deleteGoalHandler={onDeleteItem} />
          )}
        />


      </View>
      <FlatList

      />
      {/* <View style={styles.buttonContainer}>
        <TouchableHighlight style={styles.button} onPress={() => Alert.alert('Clicked')}>
          <Text style={styles.buttonText}>Click</Text>
        </TouchableHighlight>
      </View> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    marginTop: 20,
    paddingTop: 50,
    alignItems: 'center',
    justifyContent: 'center'
  },

});
