import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { Button, StyleSheet, Text, View, TouchableHighlight, Alert, TextInput } from 'react-native';

export default function App() {
  const [enteredGoalText, setEnteredGoalText] = useState('');
  const [courseGoals, setCourseGoals] = useState([]);

  function goalInputHandler(enteredText) {
    setEnteredGoalText(enteredText);
  }
  function addGoalHandler() {
    setCourseGoals(currentCourseGoals => 
      [
        ...currentCourseGoals, enteredGoalText,
      ]);
  }
  return (
    <View style={styles.container}>
      <View style={styles.flexStyle}>
        <TextInput style={styles.textInput} onChangeText={goalInputHandler} placeholder='Your course goal' />
        <View style={styles.buttonContainer}>
          <TouchableHighlight style={styles.button} onPress={addGoalHandler}>
            <Text style={styles.buttonText}>Add Goal</Text>
          </TouchableHighlight>
        </View>
      </View>
      <View style={styles.goalsContainer}>
        {courseGoals.map((goal) => 
        <View style={styles.goalItems}>
          <Text key={goal}>{goal}</Text></View>
        )}
      </View>
      {/* <View style={styles.buttonContainer}>
        <TouchableHighlight style={styles.button} onPress={() => Alert.alert('Clicked')}>
          <Text style={styles.buttonText}>Click</Text>
        </TouchableHighlight>
      </View> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 10,
  },
  flexStyle: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginVertical: 5,
  },
  textInput: {
    width: '50%',
    height: '70%',
    marginEnd: 10,
    borderWidth: 1,
    borderColor: '#cccccc',
    borderRadius: 4,
    paddingHorizontal: 4,
  },
  buttonContainer: {
    marginVertical: 10,
    backgroundColor: '#000000',
    borderRadius: 4,
    width: 120,
    height: 40,
  },
  button: {
    alignItems: 'center',
    justifyContent: 'center',
    flex: 1,
  },
  buttonText: {
    color: '#fff',
  },
  goalsContainer: {
    // flex: 1,
  },
  goalItems: {
    marginVertical: 5,
    borderRadius: 10,
    backgroundColor: 'blue',
    color: 'white',
    padding: 10,
    width: '100%',
  },
});
